E2Helper.Descriptions["capaldiGet"] = "Returns exterior shell of a capaldi when used on an occupant, the exterior itself, or the interior."
E2Helper.Descriptions["capaldiDemat"] = "Initiates dematerialisation, takes vector pos and optional angle rot, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiPhase"] = "Toggles phase, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiFlightmode"] = "Toggles flightmode, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiLock"] = "Locks it, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiPhyslock"] = "Toggles physlock, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiPower"] = "Toggles power, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiIsomorph"] = "Toggles isomorphic security systems, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiLongflight"] = "Toggles long-flight, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiMaterialise"] = "Rematerialises, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiSelfrepair"] = "Toggles self-repair, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiTrack"] = "Sets tracking entity, takes entity ent, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiSpinmode"] = "Sets spinmode, takes number spinmode (-1=anti-clockwise|0=none|1=clockwise), returns spinmode as number."
E2Helper.Descriptions["capaldiSetDestination"] = "Sets destination if in vortex, takes vector pos and optional angle rot, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiFastReturn"] = "Returns capaldi to its previous takeoff position, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiHADS"] = "Toggles HADS, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiFastDemat"] = "Quickly dematerialises capaldi, returns 1 if success or 0 if failure."
E2Helper.Descriptions["capaldiMoving"] = "Returns 1 if teleporting or 0 if not teleporting."
E2Helper.Descriptions["capaldiVisible"] = "Returns 1 if visible or 0 if invisible."
E2Helper.Descriptions["capaldiFlying"] = "Returns 1 if flying or 0 if not flying."
E2Helper.Descriptions["capaldiLocked"] = "Returns 1 if locked or 0 if not locked."
E2Helper.Descriptions["capaldiPhyslocked"] = "Returns 1 if physlocked or 0 if not physlocked."
E2Helper.Descriptions["capaldiPowered"] = "Returns 1 if powered or 0 if not powered."
E2Helper.Descriptions["capaldiIsomorphic"] = "Returns 1 if isomorphic security systems are enabled or 0 if isomorphic security systems are disabled."
E2Helper.Descriptions["capaldiLongflighted"] = "Returns 1 if long-flight is enabled or 0 if long-flight is disabled."
E2Helper.Descriptions["capaldiSelfrepairing"] = "Returns 1 if self-repairing or 0 if not self-repairing."
E2Helper.Descriptions["capaldiHealth"] = "Returns capaldi health."
E2Helper.Descriptions["capaldiLastPos"] = "Returns the vector of where the capaldi last dematerialised from."
E2Helper.Descriptions["capaldiLastAng"] = "Returns the angle of where the capaldi last dematerialised from."
E2Helper.Descriptions["capaldiTracking"] = "Returns the entity the capaldi is tracking."
E2Helper.Descriptions["capaldiInVortex"] = "Returns 1 if in vortex, or 0 if not."
E2Helper.Descriptions["capaldiIsHADS"] = "Returns 1 if HADS is enabled, or 0 if not."
E2Helper.Descriptions["capaldiPilot"] = "Returns the pilot if valid."